        if (anArray[first - 1] <= anArray[first])
        {
            first = second;
            second++;
        }
        else
        {
            int tmp = anArray[first - 1];
            anArray[first - 1] = anArray[first];
            anArray[first] = tmp;
            first -= 1;
            if (first == 0)
